				var serviceEnabled = true;
				for (var j = 0; j < objects.length; j++) {
					var po = objects[j].getReferencedObject();
					if (!po.objectDefinition.isCustomServiceDefined(customService) || !customService.isEnabled(po)) {
						serviceEnabled = false;
