Service.method:

var soapBody = content.firstChild;
if (soapBody.nodeName == "soap:Body") {
	var bodyContent = soapBody.firstChild;
	content = new flash.xml.XMLDocument(StringUtil.Singleton.unescapeText(bodyContent.toString()));
