for (var i = 0; i < nodes.length; i++) {
	var node = nodes[i];
	var textNode = node.firstChild;
	if (textNode != null && (textNode.nodeType == 3 || textNode.nodeType == 4) && node.nodeName == key) {
		return textNode.nodeValue;
	}
}
return null;


